import React, { useEffect, useState } from "react";
import { createMockUserAndList, getUserLists } from "./api";

import List from "./Components/List";
import useLocalStorage from "./Hooks/useLocalStorage";

export default function App() {
  const [user, setUser, hasCheckedLocalStorage] = useLocalStorage(
    "ez-kanban-user-id",
    null
  );
  const [lists, setLists] = useState([]);

  useEffect(() => {
    console.log("user", user);
    if (user && hasCheckedLocalStorage) {
      getUserLists(user).then(({ data }) => {
        setLists(data);
      });
    }
  }, [hasCheckedLocalStorage, user]);

  async function handleCreateUser() {
    createMockUserAndList().then((list) => {
      setLists([list]);
      setUser(list.user_id);
    });
  }

  function handleAddList() {}

  return (
    <div className="app-container">
      <h1 className="app-title">EZ Kanban</h1>
      {!user && (
        <button className="start-button" onClick={handleCreateUser}>
          Let's Get Started
        </button>
      )}
      {user && (
        <div className="content-container">
          {lists.map((list) => (
            <List key={list.id} {...list} />
          ))}
          <button
            className="add-list-button add-button"
            onClick={handleAddList}
          >
            Add a list...
          </button>
        </div>
      )}
    </div>
  );
}
